import React from "react";
import ReactDOM from "react-dom/client";
import App from "./App";

const navbar = (
  <nav>
    <h1>Bistro 3way</h1>
    <ul>
      <li>Valor</li>
      <li>Quem Somos</li>
      <li>Contato</li>
    </ul>
  </nav>
);

function Header() {
  return (
    <header>
      <nav>
        <img src="./images/logo-react.png" alt="logo react" width="40px" />
      </nav>
    </header>
  );
}

function Main() {
  return (
    <main>
      <h1>Curiosidades sobre o React</h1>
      <ul>
        <li>Foi lançado pela primeira vez em 2013</li>
        <li>Foi originalmente criado por Jordan Walke</li>
        <li>Tem mais de 100 mil estrelas no GitHub</li>
        <li>É mantido pelo Facebook</li>
        <li>
          Capacita milhares de aplicativos corporativos, incluindo aplicativos
          móveis
        </li>
      </ul>
    </main>
  );
}

function Footer() {
  return (
    <footer>
      <small>
        @ 2023 desenvolvedor Sérgio Cardoso. Todos direitos reservados{" "}
      </small>
    </footer>
  );
}

function Page() {
  return (
    <div>
      <Header />
      <Main />
      <Footer />
    </div>
  );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  //<React.StrictMode>
  //{navbar}
  //</React.StrictMode>
  <React.StrictMode>
    <Page />
  </React.StrictMode>
);
